mapper.insert:
INSERT INTO Alarm (
    user_id, sender_id, project_id, type, reference_id, content, url
) VALUES (
             #{userId}, #{senderId}, #{projectId}, #{type}, #{referenceId}, #{content}, #{url}
         )

mapper.update:
UPDATE Alarm
SET is_read = TRUE
WHERE alarm_id = #{alarmId}

UPDATE Alarm
SET deleted_at = NOW()
WHERE alarm_id = #{alarmId}
