mapper.insert:
INSERT INTO DailyReport (
    project_id, user_id, report_date, title, content, summary,
    status, commit_count, is_published, original_content, dr_file_path
) VALUES (
             #{projectId}, #{userId}, #{reportDate}, #{title}, #{content}, #{summary},
             'DRAFT', #{commitCount}, false, #{originalContent}, #{drFilePath}
         )

mapper.select:
SELECT * FROM DailyReportChatLog
WHERE report_id = #{reportId}
ORDER BY created_at ASC

SELECT * FROM DailyReportChatLog
WHERE report_id = #{reportId}
ORDER BY created_at ASC
    LIMIT #{limit} OFFSET #{offset}

SELECT daily_report_time, github_default_branch
FROM Project
WHERE project_id = #{projectId}

mapper.update:
UPDATE Project
SET daily_report_time = #{settings.daily_report_time},
    github_default_branch = #{settings.github_default_branch}
WHERE project_id = #{projectId}
