mapper.select:
SELECT * FROM DailyReport
WHERE project_id = #{projectId}
ORDER BY report_date ASC

SELECT COUNT(*)
FROM FinalReport
WHERE project_id = #{projectId}
AND created_by = #{userId}

mapper.insert:
INSERT INTO FinalReport (
    project_id, title, content, status, created_by, created_at, updated_at
) VALUES (
    #{projectId}, #{title}, #{content}, #{status}, #{createdBy}, NOW(), NOW()
)
