mapper.insert:
INSERT INTO Issue (
    project_id, title, description, status, priority, due_date, created_by
) VALUES (
             #{projectId}, #{title}, #{description}, #{status}, #{priority}, #{dueDate}, #{createdBy}
         )

mapper.select:
SELECT COUNT(*)
FROM Issue_Assignee
WHERE issue_id = #{issueId} AND user_id = #{userId}

mapper.update:
UPDATE Issue
SET status = #{status}
WHERE issue_id = #{issueId}

mapper.select:
SELECT COUNT(*)
FROM Issue_Assignee
WHERE issue_id = #{issueId}

mapper.delete:
DELETE FROM Issue_Assignee
WHERE issue_id = #{issueId}
  AND user_id = #{userId}

mapper.select:
SELECT
i.issue_id,
i.title,
i.status,
i.priority,
i.due_date,
i.created_by,
i.created_at,
u.user_id AS assignee_id,   u.name    AS assignee_name  FROM Issue i
LEFT JOIN Issue_Assignee ia ON i.issue_id = ia.issue_id
LEFT JOIN User u ON ia.user_id = u.user_id

    i.project_id = #{projectId}
    AND i.archived_at IS NULL


        AND i.status = #{status}


        AND i.priority = #{priority}


        AND EXISTS (
        SELECT 1 FROM Issue_Assignee sub_ia
        WHERE sub_ia.issue_id = i.issue_id AND sub_ia.user_id = #{assigneeId}
        )


        AND i.created_at >= #{createdStart}


        AND i.created_at <= CONCAT(#{createdEnd}, ' 23:59:59')


        AND i.due_date >= #{dueStart}


        AND i.due_date <= CONCAT(#{dueEnd}, ' 23:59:59')


ORDER BY

    i.priority ${sortDirection}, i.created_at DESC
    i.created_at ${sortDirection}
