mapper.select:
SELECT role
FROM ProjectMember
WHERE project_id = #{projectId} AND user_id = #{userId}

mapper.update:
UPDATE Project
SET deleted_at = NULL
WHERE project_id = #{projectId}

mapper.select:
SELECT COUNT(*) FROM ProjectMember WHERE project_id = #{projectId} AND user_id IN
#{userId}
AND status = 'ACTIVE' AND deleted_at IS NULL
