mapper.insert:
INSERT INTO User(
    user_id, password_hash, name, email, reg_date, file_path, deleted_at,
    github_id, github_token
) VALUES (
    #{userId}, #{passwordHash}, #{name}, #{email}, NOW(), #{filePath}, #{deletedAt},
    #{githubId}, #{githubToken}
)

mapper.update:
UPDATE User
SET github_id = #{githubId},
    github_token = #{githubToken}
WHERE user_id = #{userId}
